package projects.ecommunicator.invoker;import java.awt.Toolkit;import java.net.URL;import javax.swing.JFrame;public class Scool extends Object {    public static void main(String[] args) {        JFrame splashFrame = null;        URL imageURL = Scool.class.getClassLoader().getResource("images/Splash.jpg");        if (imageURL != null) {            splashFrame = SplashWindow.splash(Toolkit.getDefaultToolkit().createImage(imageURL));        } else {            System.err.println("Splash image not found");        }        try {            Class.forName("projects.ecommunicator.application.WhiteBoardFrame").getMethod("main", new Class[] {String[].class}).invoke(null, new Object[] {args});        } catch (Throwable e) {            e.printStackTrace();            System.err.flush();            System.exit(10);        }        if (splashFrame != null) splashFrame.dispose();    }}